document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelector(".header-wrapper__burger--cont"),t=document.querySelector(".header-wrapper__menu");e.addEventListener("click",()=>{e.classList.toggle("burgerActive"),t.classList.toggle("menuActive")}),t.addEventListener("click",(function(){e.classList.remove("burgerActive"),t.classList.remove("menuActive")})),window.addEventListener("scroll",(function(){window.scrollY>0&&(e.classList.remove("burgerActive"),t.classList.remove("menuActive"))}))}));